#include <string>
#include <map>

using namespace std;

int main() {
    // Declare variables
    string line;
    int count = 0;

    // Open text file
    ifstream myfile("file.txt");

    // Read the file line by line
    while (getline(myfile, line)) {
        // Split the line by any whitespaces
        istringstream iss(line);
        string word;

        // Read each word and increment the count
        while (iss >> word) {
            if (word == "word") {
                count++;
            }
        }
    }

    // Output the result
    cout << "Number of occurrences of 'word': " << count << endl;

    return 0;
}

