class Queue:
    def __init__(self):
        self.items = []
        self.front = None
        self.back = None

    def enqueue(self, item):
        if self.front is None:
            self.front = None
        elif self.back is None:
            self.back = None
        else:
            self.items.append(item)

    def dequeue(self):
        if self.front is not None:
            return self.items.pop(self.front)

    def is_empty(self):
        if self.front is None and self.back is None:
            return True
        else:
            return False
