// Function to sort an array using Quicksort algorithm

public static void sort(int[] arr) {
    // Base case
    if (arr.length <= 1) {
        return;
    }

    // Pivot selection
    int pivotIndex = arr.length - 1;
    int pivotValue = arr[pivotIndex];
    int leftIndex = 0;
    int rightIndex = pivotIndex;

    // Swap elements
    while (leftIndex < rightIndex) {
        int temp = arr[leftIndex];
        arr[leftIndex] = arr[rightIndex];
        arr[rightIndex] = temp;
        leftIndex++;
        rightIndex--;
    }

    // Recursive call
    sort(arr, leftIndex, rightIndex - 1);

    // Swap elements
    int temp = arr[pivotIndex];
    arr[pivotIndex] = arr[leftIndex];
    arr[leftIndex] = temp;
}

// Recursive call to sort elements in left subarray

private static void sort(int[] arr, int leftIndex, int rightIndex) {
    if (leftIndex < rightIndex) {
        int pivotValue = arr[rightIndex];
        int temp = arr[leftIndex];
        arr[leftIndex] = arr[rightIndex];
        arr[rightIndex] = temp;

        sort(arr, leftIndex + 1, rightIndex);
        sort(arr, leftIndex, rightIndex - 1);
    }
}

